import React from "react";
import Review from "./Review";

export default class Feed extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            comments: [''],
            username: ['']
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
      handleChange(event){
        event.preventDefault();
        this.props.comments.push({
            content: this.state.comments
        })
      }
      render(){
        return(
            <div className="container">
                <Review comments={this.props.comments}/>
                <h4>What did you think?</h4>
                <form onSubmit={this.handleSubmit} type='textarea' name="review" value={this.state.comments.value} placeholder='Review'></form>
                <button className="btn btn-primary" type="submit">Submit</button>
            </div>

        )
      }
}
