import React from "react";
import { useState } from "react";
import Review from "./Review";

export default class ReviewList extends React.Component{

    onReviewSubmit(formState){
        const[reviews, setReviews] = useState([]);
        const newReviews = reviews.slice();
        newReviews.push(formState);
        setReviews(newReviews);
    }
    render(){
        return(
            <div className="container">
                {this.state.reviews.map(function(review,index){
                    return <div key={index}> {review.userReview}</div>
                })}

                <Review onReviewSubmit={(formState) => this.onReviewSubmit(formState)} />
            </div>
        )
    }
}
