import React, {useState, useEffect} from "react";
import {FaStar} from 'react-icons/fa'

const StarRating = () => {
    const [rating, setRating] = useState(null);
    const [hover, setHover]  = useState(null);
    return(
        <div>
            {[...Array(5)].map((star, i) =>{
                const ratingValue = i + i;

                return(
                    <label>
                        <input type={'radio'} name='rating' value={ratingValue}  onClick={() => setRating(ratingValue)} onMouseOver={() =>setHover(ratingValue)} onMouseOut={() =>setHover(null) }></input>
                        <FaStar className="star" color={ratingValue <= (hover || rating) ? '#ffc107' : '#e4e5e9' } size={100}/>
                    </label>
                ) 
            })}
        </div>
    )
};
export default StarRating;
